    // Filter out the starters play
    playsList = dummyData.filter(play =>  play.time_converted != null );
    console.log(playsList);

    let svg = d3.select("#" + chart_id);
    console.log(svg);


    let xScale = d3.scaleLinear()
        .domain(d3.extent(playsList.map(function (d) {
          return d.time_converted;
        })))
        .range([PADDING, WIDTH - PADDING]);

    let yScale = d3.scaleLinear()
        .domain([0, 150])                     // For now hardcode at 150 points cap
        .range([HEIGHT - PADDING, PADDING]);

    let line_home = d3.line()
        .x(function(d) { return xScale(d.time_converted); })
        .y(function(d) { return yScale(d.home_score); });


     let line_away = d3.line()
        .x(function(d) { return xScale(d.time_converted); })
        .y(function(d) { return yScale(d.away_score); });

    // Home team line
    svg.append("path")
            .attr("stroke", "green")
            .style("fill", "none")
            .attr("d", line_home(playsList));

    // Away team line
    svg.append("path")
            .attr("stroke", "red")
            .style("fill", "none")
            .attr("d", line_away(playsList));

